import React, { useState, useRef, useEffect } from 'react';

const stations = {
    Antyradio: 'https://ant-waw-01.cdn.eurozet.pl/ant-waw.mp3',
    RMF_FM: 'https://rs6-krk2.rmfstream.pl/RMFFM48',
    Radio_ZET: 'https://radiozetmp3-01.eurozet.pl:8400/;',
    Radio_Maryja: 'https://radiomaryja.fastcast4u.com/proxy/radiomaryja?mp=/1'
  };

const RadioPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [currentStation, setCurrentStation] = useState(Object.keys(stations)[0]);
  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const audioRef = useRef(null);
  const playPromiseRef = useRef(null);

  useEffect(() => {
    audioRef.current = new Audio(stations[currentStation]);
    audioRef.current.volume = volume;

    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    if (audioRef.current) {
      const wasPlaying = isPlaying;

      // Wait for any existing play promise to resolve before changing station
      if (playPromiseRef.current !== null) {
        playPromiseRef.current.then(() => {
          audioRef.current.pause();
          switchStation(wasPlaying);
        }).catch(() => {
          switchStation(wasPlaying);
        });
      } else {
        audioRef.current.pause();
        switchStation(wasPlaying);
      }
    }

    function switchStation(wasPlaying) {
      audioRef.current = new Audio(stations[currentStation]);
      audioRef.current.volume = volume;

      if (wasPlaying) {
        playAudio();
      }
    }
  }, [currentStation, volume, isPlaying]);

  const togglePlayPause = () => {
    if (isPlaying) {
      // Wait for play promise to resolve before pausing
      if (playPromiseRef.current !== null) {
        playPromiseRef.current.then(() => {
          audioRef.current.pause();
          setIsPlaying(false);
        }).catch(() => {
          setIsPlaying(false);
        });
        playPromiseRef.current = null;
      } else {
        audioRef.current.pause();
        setIsPlaying(false);
      }
    } else {
      playAudio();
    }
  };

  const playAudio = () => {
    if (audioRef.current) {
      const playPromise = audioRef.current.play();

      if (playPromise !== undefined) {
        playPromiseRef.current = playPromise;

        playPromise.then(() => {
          // Playback started successfully
          setIsPlaying(true);
          playPromiseRef.current = null;
        }).catch(error => {
          // Playback failed
          console.warn('Playback failed:', error);
          setIsPlaying(false);
          playPromiseRef.current = null;
        });
      }
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  return (
    <div className="radio-player">
      <h2>Odtwarzacz Radiowy</h2>
      <select value={currentStation} onChange={(e) => setCurrentStation(e.target.value)}>
        {Object.keys(stations).map((station) => (
          <option key={station} value={station}>
            {station}
          </option>
        ))}
      </select>
      <button onClick={togglePlayPause}>
        {isPlaying ? 'Pauza' : 'OdtwÃ³rz'}
      </button>
      <div>
        <label>GÅoÅnoÅÄ: </label>
        <input 
          type="range" 
          min="0" 
          max="1" 
          step="0.01" 
          value={volume} 
          onChange={handleVolumeChange} 
        />
      </div>
      <div className="date-time">
        <p>Data: {currentDateTime.toLocaleDateString()}</p>
        <p>Godzina: {currentDateTime.toLocaleTimeString()}</p>
      </div>
    </div>
  );
};

export default RadioPlayer;
